import React, {useState, useEffect, useContext} from 'react';
import {createStackNavigator} from "@react-navigation/stack";
import {NavigationContainer} from "@react-navigation/native";
import {Login} from "../screens/Login";
import Regform from "../screens/Regform";
import {Container} from "../components/Container";
import ActivityIndicator from "react-native-web/dist/exports/ActivityIndicator";
import {AsyncStorage} from "react-native-web";
import {AuthContext} from "./AuthProvider";
import {Text} from "react-native";
import {AppTabs} from "./AppTabs";


const Stack = createStackNavigator();

export default function Routes() {
    const { user, login } = useContext(AuthContext);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        AsyncStorage.getItem("user")
            .then(useString => {
                if (useString) {
                    login();
                }
                setLoading(false);
            })
            .catch(err => {
                console.log(err);
                setLoading(false);
            })
    }, []);

    if(loading) {
        return (
            <Container>
                <ActivityIndicator size="large"/>
            </Container>
        )
    }

    return (
        <NavigationContainer >
            {user ? (
                    <AppTabs/>
                ) : (
                <Stack.Navigator initialRouteName="Login" screenOptions={{ header: () => null }}>
                    <Stack.Screen options={{ headerTitle: "Belépés"}} name="Login" component={Login}/>
                    <Stack.Screen name="Regform" component={Regform}/>
                </Stack.Navigator>
                )}
        </NavigationContainer>
    );

}
