import React from 'react';
import {Keyboard, StyleSheet, Text, TextInput, TouchableWithoutFeedback, View} from 'react-native';
import {Formik} from 'formik';
import * as yup from 'yup';
import FlatButton from "../components/Button";

const regSchema = yup.object({
    phone: yup.string()
        .required('Telefonszám megadása kötelező!'),
    email: yup.string()
        .email('Kérem, helyes emailcímet adjon meg!')
        .required('Emailcím megadása kötelező!'),
    name: yup.string()
        .required('Név megadása kötelező!'),
    zip: yup.string()
        .required('Település megadása kötelező!')
});

export default function Regform() {

    return (
        <View>
            <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
                <View>
                    <View>
                        <Text style={styles.header}>REGISZTRÁCIÓ, FELIRATKOZÁS</Text>
                    </View>
                    <View style={styles.regform}>
                        <Formik
                            initialValues={{ phone: '', email: '', name: '', place: '' }}
                            validationSchema={regSchema}
                            onSubmit={(values, actions) => {
                                actions.resetForm();
                                console.log(values);
                            }}>
                            {(props) => (
                                <View>
                                    <TextInput style={styles.textinput}
                                               placeholder="Név - vezetéknév, keresztnév"
                                               value={props.values.name}
                                               onChangeText={props.handleChange('name')}
                                               onBlur={props.handleBlur('name')}
                                               underlineColorAndroid={'transparent'} />
                                    <Text style={styles.errorText} >{props.touched.name && props.errors.name}</Text>
                                    <TextInput style={styles.textinput}
                                               placeholder="E-mail cím"
                                               value={props.values.email}
                                               onChangeText={props.handleChange('email')}
                                               onBlur={props.handleBlur('email')}
                                               underlineColorAndroid={'transparent'} />
                                    <Text style={styles.errorText} >{props.touched.email && props.errors.email}</Text>
                                    <TextInput style={styles.textinput}
                                               placeholder="Telefonszám"
                                               value={props.values.phone}
                                               onChangeText={props.handleChange('phone')}
                                               onBlur={props.handleBlur('phone')}
                                               underlineColorAndroid={'transparent'} />
                                    <Text style={styles.errorText} >{props.touched.phone && props.errors.phone}</Text>
                                    <TextInput style={styles.textinput}
                                               placeholder="Irányítószám"
                                               value={props.values.zip}
                                               onChangeText={props.handleChange('zip')}
                                               onBlur={props.handleBlur('zip')}
                                               underlineColorAndroid={'transparent'} />
                                    <Text style={styles.errorText} >{props.touched.place && props.errors.place}</Text>
                                    <FlatButton text="Mehet" onPress={props.handleSubmit} />
                                </View>
                            )}
                        </Formik>
                    </View>
                </View>
            </TouchableWithoutFeedback>
        </View>
    );
}

const styles = StyleSheet.create({
    regform: {
        alignSelf: 'stretch',
        marginRight: 10,
        marginLeft: 10
    },
    header: {
        alignSelf: 'stretch',
        fontSize: 20,
        padding: 10,
        marginBottom: 40,
    },
    textinput: {
        alignSelf: 'stretch',
        height: 40,
        marginBottom: 20,
        borderBottomColor: '#9b9b9b',
        borderBottomWidth: 1
    },
    errorText: {
        color: '#D50000',
        fontWeight: 'bold',
        marginBottom: 8,
        textAlign: 'center'
    }
});
